DROP TABLE IF EXISTS `news`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
 SET character_set_client = utf8mb4 ;
CREATE TABLE `news` (
  `id_news` int(11) NOT NULL AUTO_INCREMENT,
  `id_branch_category` int(11) NOT NULL,
  `title` longtext,
  `context` longtext,
  `id_journalist` int(11) DEFAULT NULL,
  `id_admin` int(11) DEFAULT NULL,
  `status` tinyint(4) DEFAULT NULL,
  `publish_date` datetime DEFAULT NULL,
  `latest_update` datetime DEFAULT NULL,
  `id_typeofnews` int(11) NOT NULL,
  `views` int(11) DEFAULT '0',
  `multimedia` longtext,
  `describ_multimedia` longtext,
  `id_gocnhin` int(11) DEFAULT NULL,
  PRIMARY KEY (`id_news`),
  KEY `id_branch_category` (`id_branch_category`),
  KEY `id_typeofnews` (`id_typeofnews`),
  KEY `news_ibfk_4` (`id_admin`),
  KEY `news_ibfk_3` (`id_journalist`),
  KEY `id_gocnhin` (`id_gocnhin`),
  CONSTRAINT `news_ibfk_1` FOREIGN KEY (`id_branch_category`) REFERENCES `branch_category` (`id_branch_category`),
  CONSTRAINT `news_ibfk_3` FOREIGN KEY (`id_journalist`) REFERENCES `users` (`id_user`),
  CONSTRAINT `news_ibfk_4` FOREIGN KEY (`id_admin`) REFERENCES `users` (`id_user`),
  CONSTRAINT `news_ibfk_5` FOREIGN KEY (`id_typeofnews`) REFERENCES `typeofnews` (`id_typeofnews`),
  CONSTRAINT `news_ibfk_6` FOREIGN KEY (`id_gocnhin`) REFERENCES `gocnhin` (`id_gocnhin`)
) ENGINE=InnoDB AUTO_INCREMENT=70 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci;
